#Create default values
DEFAULT_old_content_id_text = ''
DEFAULT_button1_text = 'Good morning'
DEFAULT_button2_text = 'I wish I could be there'
DEFAULT_button3_text = 'I miss you'
DEFAULT_button4_text = 'Love ya'
DEFAULT_code_insert = ''
DEFAULT_image_file = 'http://www.iappedyou.com/images/IMG_2957_2.jpg'
DEFAULT_audio_source1 = 'data:audio/mp3;base64,'
DEFAULT_audio_source2 = 'data:audio/mp3;base64,'
DEFAULT_audio_source3 = 'data:audio/mp3;base64,'
DEFAULT_audio_source4 = 'data:audio/mp3;base64,'


#Create unique content key
def content_key(content_id=DEFAULT_code_insert):
    return ndb.Key('Content ID', content_id)


#Define valuable formats
class Content(ndb.Model):
    button1 = ndb.StringProperty()
    button2 = ndb.StringProperty()
    button3 = ndb.StringProperty()
    button4 = ndb.StringProperty()
    image = ndb.BlobProperty()
    audio1 = ndb.BlobProperty()
    audio2 = ndb.BlobProperty()
    audio3 = ndb.BlobProperty()
    audio4 = ndb.BlobProperty()
    code = ndb.StringProperty()


