#Saving new content
class UploadHandler(blobstore_handlers.BlobstoreUploadHandler):
    def post(self):
        if not self.get_uploads('img'):
            blob_url = "http://fabricworkshopandmuseum.org/images/noMediaUploaded.png"
        else:
            upload_image = self.get_uploads('img')
            blob_info = upload_image[0]
            blob_url = images.get_serving_url(blob_info.key())

        CHARACTER_SETS = {
                'alpha_uppercase':                                      'ABCDEFGHIJKLMNOPQRSTUVWXYZ',                                       # 26
                'alpha_numeric_uppercase':                              'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789',                             # 36
                'alpha_hardtoconfuse_uppercase':                        'ABCDEFGHJKLMNPQRSTUVWXYZ',                                         # 24 --- Omits: I, O
                'alpha_numeric_hardtoconfuse_uppercase':                'ABCDEFGHJKLMNPQRSTUVWXYZ23456789',                                 # 32 --- Omits: I, O, 0, 1
                'alpha_consonants_uppercase':                           'BCDFGHJKLMNPQRSTVWXYZ',                                            # 21 --- Omits: A, E, I, O, U
                'alpha_numeric_consonants_uppercase':                   'BCDFGHJKLMNPQRSTVWXYZ0123456789',                                  # 31 --- Omits: A, E, I, O, U
                'alpha_consonants_hardtoconfuse_uppercase':             'BCDFGHJKLMNPQRSTVWXYZ',                                            # 21 --- Omits: A, E, I, O, U
                'alpha_numeric_consonants_hardtoconfuse_uppercase':     'BCDFGHJKLMNPQRSTVWXYZ23456789',                                    # 29 --- Omits: A, E, I, O, U, 0, 1
            }
                
        greetings = True
        
        while greetings:
            new_content_id = ''.join(random.choice(CHARACTER_SETS['alpha_numeric_hardtoconfuse_uppercase']) for _ in range(6))
            content = Content(parent=content_key(new_content_id))
        
            greetings_query = Content.query(ancestor=content_key(new_content_id))
            greetings = greetings_query.fetch(1)
        
        audio1_value = str(self.request.get('audio_url1'))
        audio2_value = str(self.request.get('audio_url2'))
        audio3_value = str(self.request.get('audio_url3'))
        audio4_value = str(self.request.get('audio_url4'))
        
        if audio1_value.endswith('='):
            audio1_value = audio1_value[:-1]
        if audio2_value.endswith('='):
            audio2_value = audio2_value[:-1]
        if audio3_value.endswith('='):
            audio3_value = audio3_value[:-1]
        if audio4_value.endswith('='):
            audio4_value = audio4_value[:-1]

        content.button1 = self.request.get('button1')
        content.button2 = self.request.get('button2')
        content.button3 = self.request.get('button3')
        content.button4 = self.request.get('button4')
        content.image = blob_url
        content.audio1 = quopri.decodestring(audio1_value)
        content.audio2 = quopri.decodestring(audio2_value)
        content.audio3 = quopri.decodestring(audio3_value)
        content.audio4 = quopri.decodestring(audio4_value)
        content.code = new_content_id

        content.put()

        load_it = '/share?id={0}'.format(new_content_id)
        self.redirect(load_it)
#End saving new content


#Loading existing content
class LoadExistingCode(webapp2.RequestHandler):
    def get(self):

        old_content_id = self.request.get('id')
        old_content_id = old_content_id.upper()
        
        greetings_query = Content.query(ancestor=content_key(old_content_id))
        greetings = greetings_query.fetch(1)

        for content in greetings:
            button1_value = cgi.escape(content.button1)
            button2_value = cgi.escape(content.button2)
            button3_value = cgi.escape(content.button3)
            button4_value = cgi.escape(content.button4)
            image_file = cgi.escape(content.image)
            audio1_value = cgi.escape(content.audio1)
            audio2_value = cgi.escape(content.audio2)
            audio3_value = cgi.escape(content.audio3)
            audio4_value = cgi.escape(content.audio4)
            code_value = cgi.escape(content.code)

        upload_url = blobstore.create_upload_url('/upload')

        template_values = {
            'upload_url': upload_url,
            'old_content_id_text': old_content_id,
            'button1_text': button1_value,
            'button2_text': button2_value,
            'button3_text': button3_value,
            'button4_text': button4_value,
            'image_file' : image_file,
            'audio_source1' : audio1_value,
            'audio_source2' : audio2_value,
            'audio_source3' : audio3_value,
            'audio_source4' : audio4_value,

        }

        template = JINJA_ENVIRONMENT.get_template('index.html')
        self.response.write(template.render(template_values))
#End loading existing content

